mapper.select:
SELECT A.*, B.HERO_NAME, E.USER_NAME, F.LEVEL_NAME FROM GM_USER_HERO A LEFT JOIN GM_HERO_INFO B ON A.HERO_ID = B.HERO_ID LEFT JOIN GM_USER E ON A.USER_ID = E.USER_ID LEFT JOIN GM_HERO_LEVEL F ON A.HERO_LEVEL_ID = F.HERO_LEVE_ID
${ew.customSqlSegment}
ORDER BY CREATE_TIME DESC

SELECT A.USER_HERO_ID, A.HERO_ID, A.STAR_CODE, A.EXPERIENCE_OBTAIN, A.HERO_POWER, A.GROWTH_RATE, A.SKIN_TYPE, B.HERO_NAME, B.HERO_IMG_URL, B.HERO_ICON_URL, B.HERO_KEEL_URL, B.HERO_DESCRIPTION, F.LEVEL_CODE FROM GM_USER_HERO A LEFT JOIN GM_HERO_INFO B ON A.HERO_ID = B.HERO_ID LEFT JOIN GM_HERO_LEVEL F ON A.HERO_LEVEL_ID = F.HERO_LEVE_ID
AND A.`STATUS` = #{status}
AND A.USER_ID = #{userId}
AND A.STATE_PLAY = #{statePlay}

