mapper.select:
SELECT A.*, C.HERO_NAME, D.GM_STAR_CODE, E.USER_NAME, F.GM_LEVEL_NAME FROM GM_USER_HERO A LEFT JOIN GM_HERO_STAR B ON A.GM_HERO_STAR_ID = B.GM_HERO_STAR_ID LEFT JOIN GM_HERO_INFO C ON B.GM_HERO_ID = C.HERO_ID LEFT JOIN GM_STAR_INFO D ON B.GM_STAR_ID = D.GM_STAR_ID LEFT JOIN GM_USER E ON A.GM_USER_ID = E.USER_ID LEFT JOIN GM_HERO_LEVEL F ON A.GM_HERO_LEVEL_ID = F.GM_HERO_LEVE_ID
${ew.customSqlSegment}
ORDER BY CREATE_TIME DESC

SELECT A.GM_USER_HERO_ID, A.GM_HERO_STAR_ID, A.EXPERIENCE_OBTAIN, A.HERO_POWER, C.HERO_NAME, C.HERO_IMG_URL, C.HERO_ICON_URL, C.HERO_KEEL_URL, C.HERO_DESCRIPTION, F.GM_LEVEL_CODE, D.GM_STAR_CODE FROM GM_USER_HERO A LEFT JOIN GM_HERO_STAR B ON A.GM_HERO_STAR_ID = B.GM_HERO_STAR_ID LEFT JOIN GM_HERO_INFO C ON B.GM_HERO_ID = C.HERO_ID LEFT JOIN GM_STAR_INFO D ON B.GM_STAR_ID = D.GM_STAR_ID LEFT JOIN GM_HERO_LEVEL F ON A.GM_HERO_LEVEL_ID = F.GM_HERO_LEVE_ID
AND A.`STATUS` = #{status}
AND A.GM_USER_ID = #{gmUserId}
AND A.STATE_PLAY = #{statePlay}

