mapper.select:
SELECT COUNT(*) AS HERO_FRAG_NUM, AVG(A.SCALE) SCALE, B.HERO_FRAG_ICON_URL, C.HERO_NAME FROM GM_USER_HERO_FRAG A LEFT JOIN GM_HERO_FRAG B ON A.HERO_FRAG_ID = B.HERO_FRAG_ID LEFT JOIN GM_HERO_INFO C ON B.HERO_INFO_ID = C.HERO_ID
AND A.`STATUS` = #{status}
AND A.USER_ID = #{userId}
AND C.HERO_ID = #{heroId}
GROUP BY B.HERO_FRAG_ICON_URL,C.HERO_NAME

mapper.update:
UPDATE GM_USER_HERO_FRAG

    `STATUS` = '2',
    UPDATE_TIME = NOW(),
    UPDATE_TIME_TS = #{updateTimeTs},

WHERE
GM_USER_HERO_FRAG_ID IN (
    SELECT
    GM_USER_HERO_FRAG_ID
    FROM
    (
    SELECT
    A.USER_HERO_FRAG_ID
    FROM
    GM_USER_HERO_FRAG A
    LEFT JOIN GM_HERO_FRAG B ON A.HERO_FRAG_ID = B.HERO_FRAG_ID
    LEFT JOIN GM_HERO_INFO C ON B.HERO_INFO_ID = C.HERO_ID


            AND A.`STATUS` = #{status}


            AND A.USER_ID = #{userId}


            AND C.HERO_ID = #{heroId}


    LIMIT #{heroFragNum}
    ) AS TT
);
